targets.query:
// press_BMP388 e press_BMP280 nas últimas 24h
from(bucket: "weather")
  |> range(start: -24h)
  |> filter(fn: (r) => r._measurement == "weather_station_v4")
  |> filter(fn: (r) => r._field == "Press_BMP388" or r._field == "Press_BMP280")
  |> filter(fn: (r) => r._value <= 5000)

// press_BMP388 e press_BMP280 nas últimas 24h
from(bucket: "weather")
  |> range(start: -24h)
  |> filter(fn: (r) => r._measurement == "air_quality_v3")
  |> filter(fn: (r) => r._field == "pm10" or r._field == "pm25")
  |> filter(fn: (r) => r._value <= 5000)
